// Normaliza entrada y periodo
const b = $json.body || $json || {};
const toEmail = (b.toEmail || 'rubencereceda23@gmail.com') + '';
const subject = (b.subject || 'Resumen ventas últimos 7 días') + '';

function iso(d){ return new Date(d).toISOString(); }
const now = new Date();
const defaultFrom = new Date(Date.now() - 7*24*60*60*1000);
let from = b.from ? new Date(b.from) : defaultFrom;
let to = b.to ? new Date(b.to) : now;
if (isNaN(from)) from = defaultFrom;
if (isNaN(to)) to = now;

return [{ json: { toEmail, subject, periodo: { from: iso(from), to: iso(to) } } }];

// Lee params de 'Preparar parámetros' y ventas de 'Obtener ventas (todas)'
const params = ($items('Preparar parámetros')[0] || {}).json || {};
const ventasAll = ($items('Obtener ventas (todas)')[0] || {}).json;
const ventasArray = Array.isArray(ventasAll) ? ventasAll : (ventasAll?.data ?? []);

const desde = new Date(params.periodo?.from || new Date(Date.now() - 7*24*60*60*1000));
const hasta = new Date(params.periodo?.to || new Date());

const ventas = ventasArray.filter(v => {
  if (!v.fecha) return false;
  const d = new Date(v.fecha);
  return d && !isNaN(d) && d >= desde && d <= hasta;
});

let ingresoTotal = 0; let numVentas = ventas.length; let unidades = 0;
const porProducto = new Map();
const porCliente = new Map();

for (const v of ventas) {
  const cantidad = Number(v.cantidad ?? 1);
  const total = v.total != null ? Number(v.total) : Number(cantidad * Number(v.precio_unitario ?? 0));
  ingresoTotal += isNaN(total) ? 0 : total;
  unidades += isNaN(cantidad) ? 0 : cantidad;

  const nombreProducto = v.producto?.nombre || v.producto_nombre || v.nombre || v.sku || (v.id_vaper ? `Producto ${v.id_vaper}` : 'Desconocido');
  const p = porProducto.get(nombreProducto) || { unidades: 0, ingresos: 0 };
  p.unidades += isNaN(cantidad) ? 0 : cantidad;
  p.ingresos += isNaN(total) ? 0 : total;
  porProducto.set(nombreProducto, p);

  const cKey = (v.cliente ?? v.usuario ?? 'Desconocido') + '';
  porCliente.set(cKey, (porCliente.get(cKey) || 0) + (isNaN(total) ? 0 : total));
}

const ticketMedio = numVentas > 0 ? ingresoTotal / numVentas : 0;
const topProductos = [...porProducto.entries()].map(([producto, s]) => ({ producto, ...s })).sort((a,b)=> b.ingresos - a.ingresos).slice(0,5);
const topClientePair = [...porCliente.entries()].sort((a,b)=> b[1] - a[1])[0] || [null, 0];

return [{ json: {
  toEmail: params.toEmail,
  subject: params.subject,
  periodo: { from: desde.toISOString(), to: hasta.toISOString() },
  resumen: { ingresoTotal, numVentas, unidades, ticketMedio },
  topProductos,
  topCliente: { nombre: topClientePair[0], gasto: topClientePair[1] },
  ventas
} }];
